import React from 'react';

const inputStyle ={
    padding: "25px 25px 25px 25px",
    border:"1px solid black", 
    borderRadius:"5px"
}

class App extends React.Component{

    constructor(props){
        super(props);

        this.state={
            generalInfo:{
                key:'',
                name:'',
                email:'',
                phoneNumber:'',
                isEditing:false,
                addAnother:false
            },
            educationInfo:{
                key:'',
                schoolName:'',
                major:'',
                yearAt:'',
                isEditing:false,
                addAnother:false
            },
            workInfo:{
                key:'',
                companyName:'',
                positionTitle:'',
                jobTasks:'',
                jobDateStart:'',
                jobDateEnd:'',
                isEditing:false,
                addAnother:false
            },
            generalInfoArr:[],
            workInfoArr:[],
            educationInfoArr:[],
            hasSubmitted:false

        }
        this.handleChangeGeneral = this.handleChangeGeneral.bind(this)
        this.handleChangeSchool = this.handleChangeSchool.bind(this)
        this.handleChangeWork =this.handleChangeWork.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
    }

    handleChangeGeneral(e){

        const value = e.target.value

        this.setState((prevState)=>({
            generalInfo:{
                ...prevState.generalInfo,
                key:Math.random().toString(36).substr(2,9),
                [e.target.name]:value,
            }
        }
        ),()=>{
            console.log(this.state.generalInfo)
        });
    }
    handleChangeSchool(e){

        const value = e.target.value

        this.setState((state)=>({
            educationInfo:{
                ...state.educationInfo,
                key:Math.random().toString(36).substr(2,9),
                [e.target.name]:value,
            }
        }
        ),()=>{
            console.log(this.state.educationInfo)
        });
    }
    handleChangeWork(e){

        const value = e.target.value

        this.setState((prevState)=>({
            workInfo:{
                ...prevState.workInfo,
                key:Math.random().toString(36).substr(2,9),
                [e.target.name]:value,
            }
        }
        ),()=>{
            console.log(this.state.workInfo)
        });
    }

    handleSubmit(e){
        e.preventDefault();
        const newItem1=this.state.generalInfo
        const newItem2=this.state.educationInfo
        const newItem3=this.state.workInfo
        const items1 = [...this.state.generalInfoArr, newItem1]
        const items2 = [...this.state.educationInfoArr,newItem2]
        const items3 = [...this.state.workInfoArr,newItem3]


        this.setState((prevState)=>({
            generalInfoArr:items1,
            educationInfoArr:items2,
            workInfoArr:items3,

            generalInfo:{
                ...prevState.generalInfo,
                name:'',
                email:'',
                phoneNumber:'',
            },
            educationInfo:{
                ...prevState.educationInfo,
                schoolName:'',
                major:'',
                yearAt:'',
            },
            workInfo:{
                ...prevState.workInfo,
                companyName:'',
                positionTitle:'',
                jobTasks:'',
                jobDateStart:'',
                jobDateEnd:'',
            },
            hasSubmitted:true
        }),()=>{
            console.log(this.state.generalInfoArr)
            console.log(this.state.workInfoArr)
            console.log(this.state.educationInfoArr)
        })
    }

    handleEdit(){
        this.setState({
            hasSubmitted:!this.state.hasSubmitted,
        })
    }

    render(){

        return(
            <form onSubmit={this.handleSubmit}>
                <GenInfo hasSubmitted={this.state.hasSubmitted} generalInfo={this.state.generalInfo} handleChange={this.handleChangeGeneral}/>
                <br />
                <EducationInfo hasSubmitted={this.state.hasSubmitted} educationInfo={this.state.educationInfo} handleChange={this.handleChangeSchool} />
                <br />
                <ExperienceInfo hasSubmitted={this.state.hasSubmitted} workInfo={this.state.workInfo} handleChange={this.handleChangeWork} />
                <br />
                {this.state.hasSubmitted?<button onClick={this.handleEdit} type='button'>Edit</button>:<div><button type='submit'>Submit!</button></div>}
            </form>

        );
    }
}

class GenInfo extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
