import React from 'react';

class App extends React.Component{

    constructor(props){
        super(props);

        this.state={
            name:'',
            email:'',
            phoneNumber:'',
            info:[],
            isEditing:false,
            schoolName:'',
            major:'',
            yearAt:'',
            companyName:'',
            positionTitle:'',
            jobTasks:'',
            jobDateStart:'',
            jobDateEnd:''

        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
    }

    handleChange(e){
        const value = e.target.value
        this.setState({
            ...this.state,
            [e.target.name]: value
        })

    }

    handleSubmit(e){
        e.preventDefault();
        this.setState({
            isEditing:!this.state.isEditing,
        })
    }

    handleEdit(){
        this.setState({
            isEditing:!this.state.isEditing,
        })
    }

    render(){

        return(
            <form onSubmit={this.handleSubmit}>
                <GenInfo isEditing={this.state.isEditing} name={this.state.name} email={this.state.email} number={this.state.phoneNumber} handleChange={this.handleChange}/>
                <br />
                <EducationInfo schoolName={this.state.schoolName} major={this.state.major} yearAt={this.state.yearAt} isEditing={this.state.isEditing} handleChange={this.handleChange} />
                <br />
                <ExperienceInfo companyName={this.state.companyName} positionTitle={this.state.positionTitle} jobTasks={this.state.jobTasks} jobDateStart={this.state.jobDateStart} jobDateEnd={this.state.jobDateEnd} isEditing={this.state.isEditing} handleChange={this.handleChange} />
                <br />
                <ReturnInfo info={this.state.info} schoolName={this.state.schoolName} major={this.state.major} yearAt={this.state.yearAt}/>
                {this.state.isEditing?<button onClick={this.handleEdit} type='button'>Edit</button>:<div><button type='submit'>Submit!</button></div>}
            </form>

        );
    }
}

class GenInfo extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        const inputInfoName = <input name="name" value={this.props.name} onChange={this.props.handleChange}></input>;
        const inputInfoEmail = <input name="email" value={this.props.email} onChange={this.props.handleChange}></input>;
        const inputInfoNumber = <input name="phoneNumber" value={this.props.number} onChange={this.props.handleChange}></input>;
        return(
            <div>
                Name:{this.props.isEditing ?<div>{this.props.name}</div>:inputInfoName}
                Email:{this.props.isEditing ?<div>{this.props.email}</div>:inputInfoEmail}
                Phone Number:{this.props.isEditing ?<div>{this.props.number}</div>:inputInfoNumber}
            </div>
        );
    }
}

class EducationInfo extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        const inputSchool = <input name="schoolName" value={this.props.schoolName} onChange={this.props.handleChange}></input>;
        const inputMajor = <input name="major" value={this.props.major} onChange={this.props.handleChange}></input>;
        const inputYears = <input name="yearAt" value={this.props.yearAt} onChange={this.props.handleChange}></input>;
        return(
            <div>
                School:{this.props.isEditing ? <div>{this.props.schoolName}</div>:inputSchool}
                Major that you studied:{this.props.isEditing ? <div>{this.props.major}</div>:inputMajor}
                Years you attened:{this.props.isEditing ? <div>{this.props.yearAt}</div>:inputYears}
            </div>
        )
    }
}

class ExperienceInfo extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        const inputCompanyName = <input name="schoolName" value={this.props.schoolName} onChange={this.props.handleChange}></input>;
        const inputPositionTitle = <input name="major" value={this.props.major} onChange={this.props.handleChange}></input>;
        const inputJobTasks = <input name="yearAt" value={this.props.yearAt} onChange={this.props.handleChange}></input>;
        const inputJobDateStart=<input></input>
        const inputJobDateEnd=<input></input>

        return(
            <div>

            </div>
        )
    }
}

class ReturnInfo extends React.Component{
    constructor(props){
        super(props)
    }
    render(){
        return(
            <div>
                <h2>Information:</h2>
                <p>{this.props.info}</p>
            </div>
        )
    }
}

export default App;
