import React from 'react';

class App extends React.Component{

    constructor(props){
        super(props);

        this.state={
            name:'',
            email:'',
            phoneNumber:'',
            info:'',
            isEditing:false,
            submitted:true
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    handleChange(e){
        const value = e.target.value
        this.setState({
            ...this.state,
            [e.target.name]: value
        })
    }

    handleSubmit(e){
        e.preventDefault();
        this.setState({
            name:e.target.name.value,
            email:e.target.email.value,
            phoneNumber:e.target.number.value,
            submitted:!this.state.submitted
        })
    }

    handleEdit(){
        this.setState({
            isEditing:!this.state.isEditing,
        })
    }

    render(){

        return(
            <form onSubmit={this.handleSubmit}>
                <GenInfo isEditing={this.state.isEditing} info={this.state.info} name={this.state.name} email={this.state.email} number={this.state.phoneNumber} handleChange={this.handleChange}/>
                <ReturnInfo info={this.state.info} />
                {this.state.submitted?<div></div>:<button onClick={this.handleEdit} type='button'>Edit</button>}
                <button type='submit'>Submit!</button>
            </form>

        );
    }
}

class GenInfo extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        const inputInfoName = <input name="name" value={this.props.name} onChange={this.props.handleChange}></input>;
        const inputInfoEmail = <input name="email" value={this.props.email} onChange={this.props.handleChange}></input>;
        const inputInfoNumber = <input name="number" value={this.props.phoneNumber} onChange={this.props.handleChange}></input>;
        return(
            <div>
                {this.props.name != '' ?<div>{this.props.name}</div>:inputInfoName}
                {this.props.email != '' ?<div>{this.props.email}</div>:inputInfoEmail}
                {this.props.number != '' ?<div>{this.props.number}</div>:inputInfoNumber}
            </div>
        );
    }
}

class ReturnInfo extends React.Component{
    constructor(props){
        super(props)
    }
    render(){
        return(
            <div>
                <h2>Information:</h2>
                <p>{this.props.info}</p>
            </div>
        )
    }
}

export default App;
